import { useState } from 'react';

function FeedbackForm({ onFeedbackSubmit }) {
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const apiKey = 'your-api-key-here';
    console.log('API Key:', apiKey); // Logging API key for debugging


    try {
      const response = await fetch('http://localhost:3001//api/list/feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ feedback }),
      });

      const data = await response.json();
      onFeedbackSubmit(data.classification);
    } catch (error) {
      console.error('Error classifying feedback:', error);
      alert('There was an error classifying your feedback. Please try again.');
    } finally {
      setLoading(false);
      setFeedback('');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Leave Feedback</h2>
      <form onSubmit={handleFeedbackSubmit}>
        <textarea
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
          rows="5"
          cols="50"
          placeholder="Enter your feedback..."
          required
        />
        <br />
        <button type="submit" style={{ marginTop: '10px' }} disabled={loading}>
          {loading ? 'Submitting...' : 'Submit Feedback'}
        </button>
      </form>
    </div>
  );
}

export default FeedbackForm;
