import { useState } from 'react';
import { Configuration, OpenAIApi } from 'openai';

function FeedbackForm({ onFeedbackSubmit }) {
  const [feedback, setFeedback] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Set up OpenAI API configuration with your API key
      const configuration = new Configuration({
        apiKey: process.env.REACT_APP_OPENAI_API_KEY, // Replace with your OpenAI API key
      });
      const openai = new OpenAIApi(configuration);

      // Call OpenAI API to classify feedback
      const response = await openai.createCompletion({
        model: 'text-davinci-003', // or any other model you'd like to use
        prompt: `Classify the following feedback into Positive, Neutral, or Negative: "${feedback}"`,
        max_tokens: 10,
        temperature: 0, // To make it deterministic
      });

      const classification = response.data.choices[0].text.trim();

      // Call the function to pass the classification back to the BuyNow component
      onFeedbackSubmit(classification);

    } catch (error) {
      console.error('Error classifying feedback:', error);
      alert('There was an error classifying your feedback. Please try again.');
    } finally {
      setLoading(false);
      setFeedback('');
    }
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Leave Feedback</h2>
      <form onSubmit={handleFeedbackSubmit}>
        <textarea
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
          rows="5"
          cols="50"
          placeholder="Enter your feedback..."
          required
        />
        <br />
        <button type="submit" style={{ marginTop: '10px' }} disabled={loading}>
          {loading ? 'Submitting...' : 'Submit Feedback'}
        </button>
      </form>
    </div>
  );
}

export default FeedbackForm;
