import { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import FeedbackForm from './FeedbackForm'; // Assuming FeedbackForm is in the same directory

function User() {
  const { state } = useLocation(); // Receive the product(s) from state
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    phone: '',
  });
  const [showFeedbackForm, setShowFeedbackForm] = useState(false); // To show feedback form after order
  const [feedbackResult, setFeedbackResult] = useState(null); // Store feedback result (Positive, Neutral, Negative)
  const navigate = useNavigate();

  // Handle form change for customer details
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle order placement
  const handleSubmit = (e) => {
    e.preventDefault();
    
    const productsToOrder = state.product ? [state.product] : state.products;

    // Create the order object with product and customer details
    const order = {
      products: productsToOrder,
      customer: formData,
      date: new Date().toLocaleString(),
    };

    // Retrieve existing orders from local storage, then update
    const orders = JSON.parse(localStorage.getItem('orders')) || [];
    orders.push(order);
    localStorage.setItem('orders', JSON.stringify(orders));

    alert('Order placed successfully!');

    if (!state.product) {
      localStorage.removeItem('cart'); // Clear the cart if user ordered all cart items
    }

    // Show feedback form after placing the order
    setShowFeedbackForm(true);
  };

  // Handle feedback submission
  const handleFeedbackSubmit = (classification) => {
    setFeedbackResult(classification); // Store the feedback result

    // After showing feedback for 5 seconds, navigate to home
    setTimeout(() => {
      navigate('/'); // Redirect to home page
    }, 5000);
  };

  return (
    <div style={{ margin: '20px' }}>
      {showFeedbackForm ? (
        <div>
          {/* Display feedback form */}
          <FeedbackForm onFeedbackSubmit={handleFeedbackSubmit} />
          
          {/* Show feedback result once feedback is submitted */}
          {feedbackResult && (
            <div>
              <h3>Feedback: {feedbackResult}</h3>
              <p>Redirecting to home in 5 seconds...</p>
            </div>
          )}
        </div>
      ) : (
        <div>
          <h3>Products:</h3>
          {/* Show either a single product or multiple products */}
          {state.product ? (
            <div style={{ border: '1px solid #ccc', padding: '10px', margin: '10px' }}>
              <h3>{state.product.title}</h3>
              <p><strong>Price:</strong> ${state.product.price}</p>
            </div>
          ) : (
            state.products.map((product, index) => (
              <div key={index} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px' }}>
                <h3>{product.title}</h3>
                <p><strong>Price:</strong> ${product.price}</p>
              </div>
            ))
          )}

          {/* Order form */}
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', width: '300px' }}>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Name"
              required
              style={{ marginBottom: '10px', padding: '5px' }}
            />
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="Address"
              required
              style={{ marginBottom: '10px', padding: '5px' }}
            />
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="Phone"
              required
              style={{ marginBottom: '10px', padding: '5px' }}
            />
            <button type="submit" style={{ padding: '5px' }}>Place Order</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default User;
