import { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function User() {
  const { state } = useLocation(); // Receive the product from the state
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    phone: '',
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Create order object with product and form details
    const order = {
      product: state.product,
      customer: formData,
      date: new Date().toLocaleString(),
    };

    // Retrieve existing orders from local storage and update
    const orders = JSON.parse(localStorage.getItem('orders')) || [];
    orders.push(order);
    localStorage.setItem('orders', JSON.stringify(orders));

    alert('Order placed successfully!');

    // Navigate to the order history page
    navigate('/');
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Place Your Order</h2>
      <p><strong>Product:</strong> {state.product.title}</p>
      <p><strong>Price:</strong> ${state.product.price}</p>

      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', width: '300px' }}>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Name"
          required
          style={{ marginBottom: '10px', padding: '5px' }}
        />
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleChange}
          placeholder="Address"
          required
          style={{ marginBottom: '10px', padding: '5px' }}
        />
        <input
          type="tel"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          placeholder="Phone"
          required
          style={{ marginBottom: '10px', padding: '5px' }}
        />
        <button type="submit" style={{ padding: '5px' }}>Place Order</button>
      </form>
    </div>
  );
}

export default User;
