import { useState, useEffect } from 'react';

function App() {
  const [products, setProducts] = useState([]); // State to store multiple products

  const fetchMoreData = async () => {
    const url = 'https://fakestoreapi.com/products'; // Fetch all products
    try {
      const response = await fetch(url);
      const parsedData = await response.json();
      setProducts(parsedData); // Set the fetched array of products to state
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchMoreData(); // Fetch data when the component mounts
  }, []);

  return (
    <div className="App">
      <h1>Product List</h1>
      {products.length > 0 ? (
        <div>
          {products.map((product) => (
            <div key={product.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px' }}>
              <h2>{product.title}</h2>
              <img src={product.image} alt={product.title} style={{ width: 'px' }} />
              <p><strong>Price:</strong> ${product.price}</p>
              <p><strong>Description:</strong> {product.description}</p>
              <p><strong>Category:</strong> {product.category}</p>
              <p><strong>Rating:</strong> {product.rating.rate} ({product.rating.count} reviews)</p>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
