import { useState, useEffect } from 'react';

function App() {
  const [categories, setCategories] = useState([]); 
  const [products, setProducts] = useState([]); 
  const [selectedCategory, setSelectedCategory] = useState(''); 

  // Fetch categories
  const fetchCategories = async () => {
    const url = 'https://fakestoreapi.com/products/categories'; 
    try {
      const response = await fetch(url);
      const parsedData = await response.json();
      setCategories(parsedData); 
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  // Fetch products by category
  const fetchProductsByCategory = async (category) => {
    const url = `https://fakestoreapi.com/products/category/${category}`; 
    try {
      const response = await fetch(url);
      const parsedData = await response.json();
      setProducts(parsedData); 
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  useEffect(() => {
    fetchCategories(); 
  }, []);

  // Handle category selection
  const handleCategoryClick = (category) => {
    setSelectedCategory(category); 
    fetchProductsByCategory(category); 
  };

  return (
    <div className="App">

      <h1>Categories</h1>
      {/* Render categories */}
      <ul>
        {categories.map((category, index) => (
          <li key={index} onClick={() => handleCategoryClick(category)} style={{ cursor: 'pointer', color: selectedCategory === category ? 'blue' : 'black' }}>
            {category}
          </li>
        ))}
      </ul>

      <h2>Products {selectedCategory && `in ${selectedCategory}`}</h2>
      {/* Render products */}
      {products.length > 0 ? (
        <div>
          {products.map((product) => (
            <div key={product.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px' }}>
              <h2>{product.title}</h2>
              <img src={product.image} alt={product.title} style={{ width: '70px' }} />
              <p><strong>Price:</strong> ${product.price}</p>
              <p><strong>Description:</strong> {product.description}</p>
              <p><strong>Category:</strong> {product.category}</p>
              <p><strong>Rating:</strong> {product.rating.rate} ({product.rating.count} reviews)</p>
            </div>
          ))}
        </div>
      ) : (
        <p>{selectedCategory ? 'Loading products...' : 'Please select a category'}</p>
      )}
    </div>
  );
}

export default App;
